INSERT INTO `info_page_content` (`id`, `id_info_page`, `info_title`, `info_text`, `weight`) VALUES
(1, 1, 'DA LI MOGU DA PLATIM PROIZVODE NA MOJ ALAT INTERNET PRODAVNICI PLATNIM / KREDITNIM KARTICAMA I KOJIM?', 'Na Moj alat internet  prodavnici kupljene proizvode za sada niste u mogu?nosti da platite platnim/kreditnim karticama. Platnim/kreditnim karticama mogu?e je pla?ati u našem maloprodajnom objektu u Subotici, na adresi Majšanski put 128.', 0),
(2, 1, 'KAD SAM PORUČIVAO PROIZVOD KOD VAS, ZABORAVIO SAM DA NAZNAČIM NAČIN PLAĆANJA (TRANSPORTA). DA LI ĆE TAJ PROIZVOD STIĆI NA MOJU ADRESU, A AKO NEĆE, ŠTA DA URADIM?', 'Ukoliko ne popunite neko od obaveznih polja (označeni su *) naš program Vam neće dozvoliti da pređete u sledeći korak kupovine, vratiće Vas na prethodni i upozoriti da popunite polje koje nedostaje.', 0),
(3, 1, 'ŠTA JE KORPA?', 'Korpa predstavlja internet servis za kupovinu, koji Vam omogućava da kupujete po istom principu kao i sa pravom korpom u prodavnici. Korpa služi da izaberete proizvode koje želite da kupite. \nDok pregledate proizvode na sajtu možete ih ubaciti u korpu klikom na dugme "Dodaj u korpu". Na taj način izabrani proizvodi biće zapamćen dok pregledate sajt. Ubacivanje proizvoda u korpu Vas ne obavezuje da taj proizvod kupite, a moguće je i izbacivanje proizvoda iz korpe. U svakom trenutku možete pregledati sadržaj svoje korpe klikom na link "Korpa" koji se nalazi u zaglavlju strane.', 0),
(4, 1, 'REGISTROVAN SAM KAO VAŠ KUPAC I KUPOVAO SAM KOD VAS, MEĐUTIM, MORAO SAM DA PROMENIM INTERNET PROVAJDERA. DA LI NOVONASTALA SITUACIJA DONOSI NEKE PROMENE, TJ. ŠTA TREBA DA RADIM U OVOM SLUČAJU?', 'Najbolje rešenje je da nas obavestite o tome i ponovo se registrujete sa novom e-mail adresom. Ili nas možete kontaktirati kako bismo izvršili preregistraciju. Sve drugo, ostaje isto.', 0),
(5, 1, 'KUPOVINU OBAVLJAM ZA SOPSTVENU FIRMU. DA LI MOGU DA PLATIM SA RAČUNA FIRME?', 'Možete platiti sa računa firme. Potrebno je da u okviru načina plaćanja odaberete opciju virmansko plaćanje ili plaćanje preko računa po predračunu, popunite nalog za prenos i izvršite prenos sredstava.', 0),
(6, 1, 'GREŠKOM SAM NARUČIO PROIZVOD. DA LI MOGU DA OTKAŽEM PORUDŽBINU?', 'U ovom slučaju, potrebno je da nas što pre obavestite kako bi stornirali Vašu narudžbu. Možete nas obavestiti preko email adrese ili putem telefona .', 0),
(7, 1, 'ŠTA SE DEŠAVA AKO ROBA KOJU DOBIJEM BUDE OŠTEĆENA? DA LI JE MOGU ZAMENITI I NA KOJI NAČIN?', 'Ukoliko uočite, dok je kurir prisutan, da je  isporučeni proizvod  oštećen ili mu nedostaju neki delovi (nekompletan je) problem se mora evidentirati na licu mesta, ispred kurira, tako što ćete proveriti robu i ukoliko nešto nije u redu, odmah prijaviti neispravnost. U ovom slučaju se popunjava  Zapisnik o oštećenju koji moraju da potpišu i kupac i kurir. Oštećenja uočena na licu mesta se rešavanju sa kurirskom službom. Zato je najbolje da pred kurirom pregledate paket kako bi bili sigruni da nije oštećen jer se naknadne reklamacije ne prihvataju.', 0),
(8, 1, 'DA LI MOGU KARTICOM DA PLATIM NA RATE, NA KOLIKO, I DA LI SE TADA CENA KOJU TREBA DA PLATIM MENJA?', 'Na našem sajtu plaćanje platnim karticama još uvek nije moguće, samim tim ni plaćanje karticom na rate. Platnim/kreditnim karticama moguće je plaćati našem maloprodajnom objektu u Subotici.', 0),
(9, 1, 'DA LI JE MOGUĆA ISPORUKA VAN SRBIJE?', 'Za sada ne postoji mogu?nost isporuke robe van teritorije Srbije. Za sada ne postoji mogućnost isporuke robe van teritorije Srbije. ', 0),
(10, 1, 'ŽELIM DA OBRADUJEM NEKOG I POŠALJEM MU POKLON NA KUĆNU ADRESU. DA LI JE TO MOGUĆE, I NA KOJI NAČIN?', 'Dovoljno je da izaberete željeni proizvod i u napomeni porudžbine upišete adresu na koju želite da pošaljete poklon. Ono na šta trebate obratiti pažnju je da nikako ne smete odabrati plaćanje pouzećem jer će tada račun stići osobi kojoj želite da pošaljete poklon. Zato odaberite neki drugi način plaćanja. Čim budemo proknjižili Vašu uplatu poslaćemo poklon osobi kojoj želite.', 0),
(11, 1, 'KAKO DA ZNAM DA SAM SIGURNO OBAVIO PORUČIVANJE?', 'Kada kliknete na dugme „Poruči“ na ekranu će Vam se pojaviti poruka „Vaša porudžbina je uspešno kompletirana“ i na e-mail koji ste upisali i koji koristite za kupovinu stići će Vam potvrda porudžbine sa svim proizvodima koje ste poručili i cenom tih proizvoda.', 0),
(12, 1, 'UNEO SAM POGREŠAN PODATAK O SEBI I TEK KASNIJE UVIDEO GREŠKU. KAKO MOGU TO DA ISPRAVIM?', 'Svoje podatke možete menjati kada se sa svojim korisničkim imenom i lozinkom ulogujete na naš sajt i pristupite stranici moj profil. Na ovom mestu, takođe, možete menjati i druge podatke vezane lično za Vas, pregledati svoje porudžbine i njihove statuse. Korisničko ime ne može da se menja, birajte ga pažljivo.', 0),
(13, 1, 'DA LI MOGU ONLINE DA NARUČIM PROIZVOD, A DA GA NEGDE LIČNO PREUZMEM?', 'Da, možete preuzeti u našem maloprodajnom objektu u Subotici. Potrebno je samo da prilikom poručivanja naglasite gde želite preuzeti proizvod.', 0),
(14, 1, 'KOLIKO DUGO VAŽI MOJA REZERVACIJA?', 'Robu koju poručite preko našeg sajta, čuvaćemo na rezervaciji 2 dana. Do tada očekujemo Vašu uplatu. U suprotnom, porudžbinu ćemo stornirati i smatrati da ste od nje odustali. U ovom roku, robu koju čuvamo na rezervaciji čuvaćemo po cenama koje su bile u trenutku Vašeg poručivanja, bez obzira ako se cena u međuvremenu promenila. \nNaravno, ovo važi za sve porudžbine kod kojih se očekuje prvobitna uplata. U slučaju da plaćate pouzećem, robu vam šaljemo prvom isporukom (u zavisnosti od samog roka isporuke poručenih artikala\n', 0),
(15, 1, 'IMAM TEKUĆI RAČUN U BANCI. DA LI MOGU ONLINE DA KUPIM PROIZVODE PREKO TOG RAČUNA?', 'Ukoliko želite da platite preko svog računa direktno sa svog računara koristeći opciju e-banking svoje banke onda treba da za način plaćanja odaberete Uplatnicom / nalogom za prenos unesete podatke u Vaš sistem i platite. Ukoliko želite da lično platite u banci, a opet sa Vašeg tekućeg računa, onda opet odaberete način placanja Uplatnicom/nalogom za prenos odštampate ili prepišete podatke, ponesete ih sa sobom i uplatite lično u banci nalogom za prenos sa Vašeg tekućeg računa.', 0),
(16, 2, 'PLAĆANJE PREKO RAČUNA', 'Registrujte se i popunite sva potrebna polja. Izaberite proizvode koje želite da kupite i nakon potvrde kupovine stiće vam na mail potvrda narudžbe na osnovu koje možete da izvršite avansnu uplatu. Nakon primljene uplate robu vam šaljemo na vašu adresu brzom poštom.', 0),
(17, 2, 'PLAĆANJE POUZEĆEM', 'kod plaćanja pouzećem, naručenu robu plaćate prilikom isporuke. Prilikom poručivanja robe potrebno je da u koraku odabira načina plaćanja odaberete "gotovinski". Omogućena je besplatna isporuka za sve porudzbine čiji iznos prelazi 20.000 din . Za porudzbine manje vrednosti cena isporuke zavisi od težine paketa. Paketi se šalju brzom poštom Aks i Bex.', 0),
(18, 2, 'VALUTA PLAĆANJA', 'Moguće je plaćanje samo u dinarskoj valuti.', 0),
(19, 3, 'ZAŠTITA PRIVATNOSTI PODATAKA', 'Da bismo uspešno obradili Vašu porudžbinu potrebni su nam Vaše ime i prezime, adresa, e-mail i telefon. Uz pomoć tih podataka bićemo u mogućnosti da Vam isporučimo željenu robu, kao i da Vas obavestimo o trenutnom statusu porudžbine. U naše ime obavezujemo se da ćemo čuvati privatnost svih naših kupaca. Prikupljamo samo neophodne, osnovne podatke o kupcima/korisnicima i podatke neophodne za poslovanje i informisanje korisnika u skladu sa dobrim poslovnim običajima i u cilju pružanja kvalitetne usluge. Svi podaci o kupcima/korisnicima se strogo čuvaju i dostupni su samo zaposlenima kojima su podaci nužni za obavljanje posla. Svi zaposleni u firmi Zim Commerce (vlasnik internet prodavnice mojalat.rs) odgovorni su za poštovanje načela zaštite privatnosti. Obavezujemo se da prikupljene podatke ne koristimo ni u koje druge svrhe, niti ih prosleđujemo trećim licima.\r\nPored navedenih prikupljamo, analiziramo i obrađujemo podatke o proizvodima koje naši posetioci traže i kupuju, kao i o stranicama koje posećuju. Te podatke koristimo da bismo poboljšali ponudu i izgled naših stranica, i omogućili Vam jednostavnije korišćenje, sigurniju i udobniju kupovinu.\r\n', 0),
(20, 3, 'SAGLASNOST I PROMENE USLOVA', 'Korišćenje naših servisa podrazumeva saglasnost korisnika sa navedenim Uslovima korišćenja. Mojalat.rs se obavezuje da će se pridržavati svega navedenog, a sve promene uslova postaju važeće tek nakon objavljivanja na ovoj strani i slanja e-mail obaveštenja svim registrovanim korisnicima.', 0),
(21, 5, 'ROKOVI I TROŠKOVI DOSTAVE', 'Omogućena je besplatna dostava robe za sve porudžbine čiji iznos prelazi 20.000 din. Za porudžbine manje vrednosti cena isporuke zavisi od težine paketa na osnovu cenovnika kurirske službe.\nRokovi isporuke zavise od raspoložive količine određenog artikla i mogućnosti njegovog nabavljanja, a u skladu sa našom poslovnom politikom, očekivani rok isporuke proizvoda iz naše ponude je 2 – 4 radna dana.', 0),
(22, 6, 'OPŠTE ODREDBE', 'Internet prodavnica mojalat.rs administrira ovu lokaciju iz svojih kancelarija u firmi Zim Commerce doo u Subotici, Srbija. Ni na koji način ne izjavljuje da su materijali ili usluge na ovoj lokaciji prikladni ili dostupni za korišćenje izvan Srbije, a pristupanje iz područja gde je njihov sadržaj nezakonit je zabranjeno. Nije dopušteno korišćenje ili izvoz, odnosno uvoz, radi izvoza materijala ili usluga na ovoj lokaciji niti bilo koje kopiranje ili prilagođavanje u suprotnosti sa važećim zakonima ili propisima uključujući, bez ograničenja, izvozne zakone i propise Srbije. Ako odlučite da pristupite ovoj lokaciji izvan Srbije, to radite na sopstvenu inicijativu i smatrate se odgovornim za poštovanje važećih lokalnih zakona. Ovi Uslovi se tumače u skladu sa zakonima Republike Srbije i neće se primenjivati načela o rešavanju neusaglašenosti zakona.\r\nMojalat.rs može revidirati ove Uslove bilo kada, ažuriranjem ovog dokumenta. Povremeno posećujte ovu stranicu da biste pregledali Uslove koji trenutno važe, zato što su oni za vas obavezujući. Određene odredbe ovih Uslova mogu se zameniti izričito navedenim pravnim obaveštenjima i uslovima dostupnim na određenim stranicama ove lokacije. \r\n', 0),
(23, 6, 'MATERIJAL KOJI PROSLEĐUJE KORISNIK (KOMENTARI I DRUGI SADRŽAJI)', 'Lični podaci koje prosledite sajtu mojalat.rs u svrhu dobijanja proizvoda ili usluga biće tretirane u skladu sa našim dokumentom o privatnosti na mreži. Zabranjeno je slanje ili prenošenje na ovu lokaciju ili sa nje bilo kojih nezakonitih, pretećih, uvredljivih, klevetničkih, opscenih, pornografskih ili drugih materijala koji su u suprotnosti s bilo kojim zakonom.\r\n\r\nMojalat.rs na ovoj lokaciji ne želi od vas da prima poverljive ili druge informacije kojima ne može slobodno da raspolaže. Svi materijali, informacije ili druga saopštenja koja prenosite ili šaljete na ovu lokaciju neće se smatrati poverljivim i onima kojima se ne može slobodno raspolagati. Mojalat.rs nema nikakve obaveze prema ovim saopštenjima. Naši zaposleni mogu da kopiraju, otkrivaju, distribuiraju, primenjuju ili na drugi način koriste saopštenja i sve podatke, slike, zvukove, tekst i sve ostale materijale u njima sadržane za bilo koje odnosno za sve komercijalne i nekomercijalne svrhe.\r\n\r\nNaravno, u ovu grupu podataka ne spadaju vaši lični podaci. Njih, u skladu sa našom Politikom privatnosti, koristimo samo za internu upotrebu, ne obelodanjujemo niti prosleđujemo trećim licima, već koristimo isključivo za potrebe obrade vaših porudžbenica.\r\n', 0),
(24, 7, 'OBRAZAC ZA JEDNOSTRANI RASKID UGOVORA', 'jednostrani-raskid-ugovora.pdf', 1),
(25, 7, 'OBRAZAC ZA REKLAMACIJU - REKLAMACIONI LIST', 'reklamacioni-list.pdf', 2),
(26, 4, 'GARANCIJA I OSTVARIVANJE PRAVA', 'Garanciju na proizvode koje kupite na našoj internet prodavnici mojalat.rs možete ostvariti uz prilog originalnog garantnog lista overenog od strane proizvođača i prodavca (ukoliko proizvod poseduje garantni list) i fiskalnog isečka. Kod svakog proizvoda je naznačeno da li ima i koliko traje garancija. \nNa električne mašine za fizička lica garancija je minimum 2 godine a za pravna lica je minimum 12 meseci.\nPod garancijom NE SPADAJU delovi koji su izloženi habanju pri normalnoj upotrebi odnosno potrošni materijal kao što su: četkice, ležajevi, stezne glave, kablovi, držači kablova, semerinzi, dihtunzi, ulje, mast. \nU slučaju povrata, servis u garantnom roku, kupac je dužan da proizvod adekvatno upakuje i pošalje nama kako bi mi dalje prosledili ovlašćenom servisu. \nU vangarantnom roku kupac sam kontaktira ovlašćeni servis i dalje sam rešava svoju reklamaciju.\n', 1);
