(1220, 1, 81, 1, 20, 'PSA 700E + 3 test', '<p>Univerzalna testera Bosch PSA 700E Easy 710W</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Karakteristike:</p>\r\n\r\n<ul>\r\n	<li>Debljina sečenja drveta: 150mm</li>\r\n	<li>Debljina sečenja čelika: 10mm</li>\r\n	<li>Hod lista testere: 20mm</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Prednosti ovog alata:</p>\r\n\r\n<ul>\r\n	<li>Testeri&scaron;e svaki materijal &ndash; bez obzira da li se radi o listovima testere za drvo, za metal ili od gipkog bimetala &ndash; svi standardni listovi testere odgovaraju</li>\r\n	<li>Velika udobnost kori&scaron;ćenja zahvaljujući modernom dizajnu univerzalne testere, optimalnom odnosu težine i veličine, ergonomskoj mekoj dr&scaron;ci i optimalnom rasporedu prekidača</li>\r\n	<li>Snažan motor od 710 W &ndash; snaga bez granice čak i za najteže primene pri renoviranju, preuređivanju i u ba&scaron;ti</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Garancija: 25 meseci</p>\r\n', 'testera, povratna, električna, univerzalna, bosch, hobi', 710, 'Struja', 'W', 12492, 0, 12492, '06033A7001', '220', '', 0, 5, 5, 1, ''),
(1221, 1, 20, 1, 20, 'GST 150 CE', '<p>Električna ubodna testera Bosch GST 150 BCE</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Karakteristike:</strong></p>\r\n\r\n<ul>\r\n	<li>Broj hodova, prazan hod: 500-3.100min-1</li>\r\n	<li>Težina bez kabla: 2.7kg</li>\r\n	<li>Dužina kabla: 4m</li>\r\n	<li>Dubina sečenja drvo/alum/čelik: 150/20/10mm</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Za&scaron;to ovaj alat?</strong></p>\r\n\r\n<ul>\r\n	<li>Sistem zatezanja lista testere bez upotrebe alata</li>\r\n	<li>Ergonomsko hvati&scaron;te sa mekim povr&scaron;inama</li>\r\n	<li>Oscilacije u 4 stepena</li>\r\n	<li>Elektronski izbor broja obrtaja</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Obim isporuke:</strong></p>\r\n\r\n<ul>\r\n	<li>Usisna mlaznica</li>\r\n	<li>Poklopac</li>\r\n	<li>Klizna papuča</li>\r\n	<li>Za&scaron;tita od kidanja materijala</li>\r\n	<li>Garnitura za usisavanje</li>\r\n	<li>Profi kofer</li>\r\n	<li>1x list testere</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Garancija: 36 meseci</p>\r\n', 'testera, ubodna, bosch, profi', 780, 'Struja', 'W', 29112, 0, 0, '0601512000', '220', '', 0, 25, 10, 1, ''),
